import React, { useState } from "react";

const categories = [
  "All",
  "Chicken",
  "Pasta",
  "Sushi",
  "Burger",
  "Salad",
  "Dessert",
  "Drinks",
];

const foodItems = [
  {
    id: 1,
    name: "Grilled Chicken",
    category: "Chicken",
    description: "Juicy grilled chicken with a side of vegetables.",
    price: "$12.99",
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 2,
    name: "Pasta Carbonara",
    category: "Pasta",
    description: "Classic Italian pasta with creamy sauce and bacon.",
    price: "$10.99",
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 3,
    name: "Sushi Platter",
    category: "Sushi",
    description: "Fresh sushi assortment with soy sauce and wasabi.",
    price: "$18.99",
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 4,
    name: "Cheeseburger",
    category: "Burger",
    description: "Juicy beef patty with melted cheese and fresh toppings.",
    price: "$8.99",
    image:
      "data:image/jpeg;base64,",
  },
];

export const Get_food_list = () => {
  const [selectedCategory, setSelectedCategory] = useState("All");

  const filteredFoods =
    selectedCategory === "All"
      ? foodItems
      : foodItems.filter((food) => food.category === selectedCategory);

  return (
    <div className="p-6 w-[95%] md:w-[90%] mt-6 mx-auto border border-gray-200 rounded-lg shadow-lg bg-white">
      {/* Scrollable Categories */}
      <div className="flex overflow-x-auto space-x-4 pb-3 scrollbar-hide">
        {categories.map((category) => (
          <button
            key={category}
            className={`px-6 py-2 rounded-full whitespace-nowrap 
        ${
          selectedCategory === category
            ? "bg-blue-500 text-white"
            : "bg-gray-200 text-gray-700"
        } 
        hover:bg-blue-400 hover:text-white transition`}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>
      <h1 className="text-3xl font-bold text-center my-6">Our Food Menu</h1>

      {/* Food Cards */}
      <div className="flex flex-wrap justify-center gap-6">
        {filteredFoods.map((food) => (
          <div
            key={food.id}
            className="bg-white rounded-lg shadow-lg overflow-hidden w-[300px] hover:scale-105 transition-transform"
          >
            <img
              src={food.image}
              alt={food.name}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h2 className="text-xl font-semibold">{food.name}</h2>
              <p className="text-gray-600 text-sm mt-2">{food.description}</p>
              <div className="mt-4 flex justify-between items-center">
                <span className="text-lg font-bold text-green-600">
                  {food.price}
                </span>
                <button className="bg-yellow-500 text-white px-4 py-2 rounded-lg hover:bg-yellow-400 transition">
                  Edit
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Get_food_list;
